import React from "react";
import Api from "../utils/Api";
import PopupWithForm from "./PopupWithForm";

function Main({
  onEditAvatarClick,
  onEditProfileClick,
  onAddPlaceClick
}) {

const [userName, setUserName] = React.useState("");
const [userInfo, setUserInfo] = React.useState("");
const [userAvatar, setUserAvatar] = React.useState("");
const [cards, setCards] = React.useState([]);

const api = new Api({
  baseUrl: "https://around.nomoreparties.co/v1/group-12",
  headers: {
    authorization: "9b991f86-368d-4ef3-963c-b91580821c46",
    "Content-Type": "application/json",
  }
});

React.useEffect((() => {
  api
    .getUserInfo()
    .then(([cardData, userData]) => {
      setUserName(userData.name);
      setUserInfo(userData.about);
      setUserAvatar(userData.avatar);
      setCards(cardData);
    })
    .catch((err) => console.log(err));
}), []);

  return (
    <>
      <main>
        <section class="profile">
          <div class="profile__group">
            <div class="profile__image-container">
              <div class="profile__overlay">
                <button
                  onClick={onEditAvatarClick}
                  type="button"
                  class="edit-button edit-button_type_profile-image"
                  id="edit-pic-button"
                ></button>
              </div>
              <img
                class="profile__image"
                src={userAvatar}
                alt="Profile Picture"
              />
            </div>
            <div class="profile__info">
              <h1 class="profile__name" placeholder="Name">{userName}</h1>
              <p class="profile__description" placeholder="Description">{userInfo}</p>
            </div>
            <button
              onClick={onEditProfileClick}
              id="edit-button"
              type="button"
              class="edit-button"
            ></button>
            <button
              onClick={onAddPlaceClick}
              type="button"
              class="add-button"
            ></button>
          </div>
        </section>

        <section class="elements"></section>

      </main>
    </>
  );
}

export default Main;
